"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import "./App.css"
import RecipeReviews from "./RecipeReviews"
import AddReview from "./AddReview"
import TopRatedRecipes from "./TopRatedRecipes"

function App() {
  const [recipes, setRecipes] = useState([])
  const [search, setSearch] = useState("")
  const [ingredient, setIngredient] = useState("")
  const [ingredientSuggestions, setIngredientSuggestions] = useState([])
  const [recipeSuggestions, setRecipeSuggestions] = useState([])
  const [showRecipeSuggestions, setShowRecipeSuggestions] = useState(false)
  const [showIngredientSuggestions, setShowIngredientSuggestions] = useState(false)
  const [error, setError] = useState(null)
  const [loading, setLoading] = useState(false)
  const [expandedRecipe, setExpandedRecipe] = useState(null)
  const [scraping, setScraping] = useState(false)
  const [scrapingMessage, setScrapingMessage] = useState("")
  const [fixingRecipe, setFixingRecipe] = useState(false)
  const [showReviews, setShowReviews] = useState(false)
  const [showAddReview, setShowAddReview] = useState(false)
  const [showTopRated, setShowTopRated] = useState(false)
  const [initialLoad, setInitialLoad] = useState(true)

  // Initial fetch of all recipes when the component mounts
  useEffect(() => {
    fetchRecipes()

    // Check if we need to auto-scrape
    // This will run once when the component mounts
    const checkRecipeCount = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/get-greatlakes")
        if (res.data && res.data["Great Lakes"] && res.data["Great Lakes"].length < 10) {
          // If we have fewer than 10 recipes, auto-scrape
          autoScrapeRecipes()
        }
      } catch (err) {
        console.error("Error checking recipe count:", err)
        // If there's an error, try to auto-scrape anyway
        autoScrapeRecipes()
      }
      setInitialLoad(false)
    }

    checkRecipeCount()
  }, [])

  // Function to auto-scrape recipes
  const autoScrapeRecipes = async () => {
    setScraping(true)
    setScrapingMessage("Automatically scraping Native American recipes... This may take a few minutes.")

    try {
      const response = await axios.get("http://127.0.0.1:5000/auto-scrape")

      if (response.data.success) {
        setScrapingMessage(`Successfully added ${response.data.new_recipes.length} new Native American recipes!`)
        // Refresh recipes after scraping
        fetchRecipes()
      } else {
        setError("Failed to scrape recipes")
      }
    } catch (err) {
      console.error("Error auto-scraping recipes:", err)
      setError("Error connecting to server. Please try again.")
    } finally {
      // Keep the success message visible for a while, then hide it
      setTimeout(() => {
        setScraping(false)
        setScrapingMessage("")
      }, 5000)
    }
  }

  // Function to fetch recipes based on search criteria
  const fetchRecipes = async () => {
    setLoading(true)
    setError(null)

    try {
      console.log(`Fetching recipes with name: "${search}" and ingredient: "${ingredient}"`)

      // Build query parameters
      const params = {}
      if (search) params.name = search
      if (ingredient) params.ingredient = ingredient

      console.log("Request params:", params)

      const res = await axios.get("http://127.0.0.1:5000/get-greatlakes", { params })

      console.log("Response data:", res.data)

      if (res.data && res.data["Great Lakes"]) {
        setRecipes(res.data["Great Lakes"])

        if (res.data["Great Lakes"].length === 0) {
          setError("No recipes found matching your search criteria.")
        }
      } else {
        console.error("Unexpected response format:", res.data)
        setError("Received an unexpected response format from the server.")
      }
    } catch (err) {
      console.error("Error fetching recipes:", err)
      setRecipes([])
      setError("Unable to connect to the server. Please make sure your Flask backend is running.")
    } finally {
      setLoading(false)
    }
  }

  // Function to fetch recipe name suggestions
  const fetchRecipeSuggestions = async (query) => {
    if (!query || query.length < 2) {
      setRecipeSuggestions([])
      return
    }

    try {
      const res = await axios.get(`http://127.0.0.1:5000/suggest-recipes?q=${query}`)
      if (res.data && res.data.suggestions) {
        setRecipeSuggestions(res.data.suggestions)
        setShowRecipeSuggestions(true)
      }
    } catch (err) {
      console.error("Error fetching recipe suggestions:", err)
      setRecipeSuggestions([])
    }
  }

  // Function to fetch ingredient suggestions
  const fetchIngredientSuggestions = async (query) => {
    if (!query || query.length < 2) {
      setIngredientSuggestions([])
      return
    }

    try {
      const res = await axios.get(`http://127.0.0.1:5000/suggest-ingredients?q=${query}`)
      if (res.data && res.data.suggestions) {
        setIngredientSuggestions(res.data.suggestions)
        setShowIngredientSuggestions(true)
      }
    } catch (err) {
      console.error("Error fetching ingredient suggestions:", err)
      setIngredientSuggestions([])
    }
  }

  // Handle recipe input change for autocomplete
  const handleRecipeChange = (e) => {
    const value = e.target.value
    setSearch(value)
    fetchRecipeSuggestions(value)
  }

  // Handle ingredient input change
  const handleIngredientChange = (e) => {
    const value = e.target.value
    setIngredient(value)
    fetchIngredientSuggestions(value)
  }

  // Handle selecting a recipe suggestion
  const selectRecipe = (suggestion) => {
    setSearch(suggestion)
    setShowRecipeSuggestions(false)
  }

  // Handle selecting an ingredient suggestion
  const selectIngredient = (suggestion) => {
    setIngredient(suggestion)
    setShowIngredientSuggestions(false)
  }

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault()
    setShowRecipeSuggestions(false)
    setShowIngredientSuggestions(false)
    fetchRecipes()
  }

  // Toggle expanded recipe
  const toggleRecipe = (idx) => {
    setExpandedRecipe(expandedRecipe === idx ? null : idx)
    setShowReviews(false)
    setShowAddReview(false)
  }

  // Handle manual scrape button click
  const handleManualScrape = () => {
    autoScrapeRecipes()
  }

  // Function to fix all missing recipes
  const handleFixMissingRecipes = async () => {
    setFixingRecipe(true)
    setError(null)

    try {
      const response = await axios.get("http://127.0.0.1:5000/fix-missing-recipes")

      if (response.data.success) {
        alert(`${response.data.message}. Failed recipes: ${response.data.failed_recipes.join(", ")}`)
        // Refresh recipes after fixing
        fetchRecipes()
      } else {
        setError("Failed to fix recipes")
      }
    } catch (err) {
      console.error("Error fixing recipes:", err)
      setError("Error connecting to server. Please try again.")
    } finally {
      setFixingRecipe(false)
    }
  }

  // Function to fix a specific recipe
  const handleFixRecipe = async (recipeName) => {
    setFixingRecipe(true)
    setError(null)

    try {
      const response = await axios.get(`http://127.0.0.1:5000/fix-recipe/${encodeURIComponent(recipeName)}`)

      if (response.data.success) {
        alert(`Successfully fixed recipe: ${recipeName}`)
        // Refresh recipes after fixing
        fetchRecipes()
      } else {
        setError(`Failed to fix recipe: ${recipeName}`)
      }
    } catch (err) {
      console.error(`Error fixing recipe ${recipeName}:`, err)
      setError(`Error fixing recipe: ${recipeName}. Please try again.`)
    } finally {
      setFixingRecipe(false)
    }
  }

  // Check if a recipe needs fixing
  const recipeNeedsFixing = (recipe) => {
    // Check if ingredients are missing or just have the placeholder
    const missingIngredients =
      !recipe.ingredients || (recipe.ingredients.length === 1 && recipe.ingredients[0] === "Ingredients not found")

    // Check if instructions are missing or just have the placeholder
    const missingInstructions =
      !recipe.instructions || (recipe.instructions.length === 1 && recipe.instructions[0] === "Instructions not found")

    return missingIngredients || missingInstructions
  }

  // Toggle reviews for a recipe
  const toggleReviews = (idx) => {
    if (expandedRecipe === idx) {
      setShowReviews(!showReviews)
      if (showAddReview) setShowAddReview(false)
    } else {
      setExpandedRecipe(idx)
      setShowReviews(true)
      setShowAddReview(false)
    }
  }

  // Toggle add review form for a recipe
  const toggleAddReview = (idx) => {
    if (expandedRecipe === idx) {
      setShowAddReview(!showAddReview)
      if (showReviews) setShowReviews(false)
    } else {
      setExpandedRecipe(idx)
      setShowAddReview(true)
      setShowReviews(false)
    }
  }

  // Toggle top rated recipes
  const toggleTopRated = () => {
    setShowTopRated(!showTopRated)
  }

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>🍲 Native American Cuisine: Great Lakes Region</h1>
        <p>Discover traditional recipes from the Great Lakes region</p>
      </header>

      <main className="app-main">
        {scraping && (
          <div className="scraping-message">
            <div className="spinner"></div>
            <p>{scrapingMessage}</p>
          </div>
        )}

        <div className="search-container">
          <h2>Search Recipes</h2>
          <form onSubmit={handleSubmit} className="search-form">
            <div className="recipe-search-container" style={{ position: "relative", flex: 1 }}>
              <input
                type="text"
                placeholder="Search by recipe name"
                value={search}
                onChange={handleRecipeChange}
                onFocus={() => search && setShowRecipeSuggestions(true)}
                onBlur={() => setTimeout(() => setShowRecipeSuggestions(false), 200)}
                className="search-input"
              />
              {showRecipeSuggestions && recipeSuggestions.length > 0 && (
                <ul className="suggestions">
                  {recipeSuggestions.map((suggestion, index) => (
                    <li key={index} onClick={() => selectRecipe(suggestion)} className="suggestion-item">
                      {suggestion}
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <div className="ingredient-search-container">
              <input
                type="text"
                placeholder="Filter by ingredient"
                value={ingredient}
                onChange={handleIngredientChange}
                onFocus={() => ingredient && setShowIngredientSuggestions(true)}
                onBlur={() => setTimeout(() => setShowIngredientSuggestions(false), 200)}
                className="search-input"
              />
              {showIngredientSuggestions && ingredientSuggestions.length > 0 && (
                <ul className="ingredient-suggestions">
                  {ingredientSuggestions.map((suggestion, index) => (
                    <li key={index} onClick={() => selectIngredient(suggestion)} className="suggestion-item">
                      {suggestion}
                    </li>
                  ))}
                </ul>
              )}
            </div>

            <button type="submit" className="search-button">
              Search
            </button>
          </form>
        </div>

        {/* Admin controls */}
        <div className="admin-controls">
          <button onClick={handleManualScrape} className="admin-button" disabled={scraping || fixingRecipe}>
            {scraping ? "Scraping..." : "Scrape More Recipes"}
          </button>
          <button onClick={handleFixMissingRecipes} className="admin-button" disabled={scraping || fixingRecipe}>
            {fixingRecipe ? "Fixing..." : "Fix All Missing Recipes"}
          </button>
          <button onClick={toggleTopRated} className="admin-button">
            {showTopRated ? "Hide Top Rated" : "Show Top Rated Recipes"}
          </button>
        </div>

        {showTopRated && <TopRatedRecipes />}

        {loading && (
          <div className="loading">
            <div className="spinner"></div>
            <p>Loading recipes...</p>
          </div>
        )}

        {error && !loading && <div className="error-message">{error}</div>}

        <div className="recipes-container">
          {recipes.map((recipe, idx) => (
            <div key={idx} className="recipe-card">
              <div className="recipe-header">
                <h2 onClick={() => toggleRecipe(idx)}>{recipe.name}</h2>
                <div className="recipe-actions">
                  {recipeNeedsFixing(recipe) && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation()
                        handleFixRecipe(recipe.name)
                      }}
                      className="fix-button"
                      disabled={fixingRecipe}
                    >
                      Fix Recipe
                    </button>
                  )}
                  <button
                    onClick={() => toggleReviews(idx)}
                    className={`review-button ${showReviews && expandedRecipe === idx ? "active" : ""}`}
                  >
                    Reviews
                  </button>
                  <button
                    onClick={() => toggleAddReview(idx)}
                    className={`add-review-button ${showAddReview && expandedRecipe === idx ? "active" : ""}`}
                  >
                    Add Review
                  </button>
                  <span onClick={() => toggleRecipe(idx)}>{expandedRecipe === idx ? "▲" : "▼"}</span>
                </div>
              </div>

              {expandedRecipe === idx && (
                <div className="recipe-details">
                  {showReviews ? (
                    <RecipeReviews recipeName={recipe.name} />
                  ) : showAddReview ? (
                    <AddReview
                      recipeName={recipe.name}
                      onReviewAdded={() => {
                        setShowAddReview(false)
                        setShowReviews(true)
                      }}
                    />
                  ) : (
                    <>
                      <div className="ingredients">
                        <h3>Ingredients</h3>
                        <ul>
                          {recipe.ingredients?.map((ing, i) => (
                            <li key={i}>{ing}</li>
                          ))}
                        </ul>
                      </div>

                      <div className="instructions">
                        <h3>Instructions</h3>
                        <ol>
                          {(recipe.instructions || recipe.Instructions || recipe.Directions)?.map((step, i) => (
                            <li key={i}>{step}</li>
                          ))}
                        </ol>
                      </div>

                      {recipe.notes && (
                        <div className="notes">
                          <h3>Notes</h3>
                          <p>{recipe.notes}</p>
                        </div>
                      )}
                    </>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>

        {recipes.length > 0 && (
          <p className="recipe-count">
            Showing {recipes.length} recipe{recipes.length !== 1 ? "s" : ""}
          </p>
        )}
      </main>

      <footer className="app-footer">
        <p>Native American Cuisine Database - Great Lakes Region</p>
      </footer>
    </div>
  )
}

export default App
